        .box {
            margin: 0;
            height: 450px;
            overflow: hidden;
            padding-left: 20%;
        }

        .box ul {
            list-style-type: none;
            margin: 0;
            padding: 0;
            position: relative;
            transition: all 0.5s linear;
            top: 0
        }

        .box ul:last-of-type {
            top: 80px
        }

        .box ul:before {
            content: "";
            display: block;
            width: 0;
            height: 100%;
            border: 1px dashed #fff;
            position: absolute;
            top: 0;
            left: 30px
        }

        .box ul li {
            margin: 20px 60px 60px;
            position: relative;
            padding: 10px 20px;
            background: rgba(255, 255, 255, 0.3);
            color: #ffffff;
            border-radius: 10px;
            line-height: 20px;
            width: 60%
        }

        .box ul li>span {
            content: "";
            display: block;
            width: 0;
            height: 100%;
            border: 1px solid #fff;
            position: absolute;
            top: 0;
            left: -30px
        }

        .box ul li>span:before,
        .box ul li>span:after {
            content: "";
            display: block;
            width: 10px;
            height: 10px;
            border-radius: 50%;
            background: #ff6d6d;
            border: 2px solid #fff;
            position: absolute;
            left: -7.5px
        }

        .box ul li>span:before {
            top: -10px
        }

        .box ul li>span:after {
            top: 95%
        }

