        }

        .box .name {
            margin-top: 10px;
            text-transform: capitalize;
            font-style: italic;
            text-align: right;
            margin-right: 20px
        }

        .box .time span {
            position: absolute;
            left: -100px;
            color: #ffffff;
            font-size: 80%;
            font-weight: bold;
        }

        .box .time span:first-child {
            top: -16px
        }

        .box .time span:last-child {
            top: 94%
        }

        .arrow {
