        (function($) {

            function EasyDropDown() {
                this.isField = true,
                    this.down = false,
                    this.inFocus = false,
                    this.disabled = false,
                    this.cutOff = false,
                    this.hasLabel = false,
                    this.keyboardMode = false,
                    this.nativeTouch = true,
                    this.wrapperClass = 'dropdown',
                    this.onChange = null;
            };

            EasyDropDown.prototype = {
                constructor: EasyDropDown,
