                            };
                            if ($option.hasClass('label') && i == 0) {
                                self.hasLabel = true;
                                self.label = $option.text();
                                $option.attr('value', '');
                            } else {
                                self.options.push({
                                    domNode: $option[0],
                                    title: $option.text(),
                                    value: $option.val(),
                                    selected: $option.is(':selected')
                                });
                            };
                        });
