      <script>
        // Menangani klik pada item menu yang memiliki submenu
        document.querySelectorAll('.menu-item-has-children > a').forEach(item => {
            item.addEventListener('click', function(event) {
                event.preventDefault(); // Mencegah tautan default
                const submenu = this.nextElementSibling;

                // Sembunyikan submenu lainnya
                document.querySelectorAll('.sub-menu').forEach(sub => {
                    if (sub !== submenu) {
                        sub.style.display = 'none';
                    }
                });

                // Toggle submenu yang diklik
                if (submenu.style.display === 'block') {
                    submenu.style.display = 'none';
                } else {
                    submenu.style.display = 'block';
                }
            });
        });

        // Menyembunyikan submenu saat mengklik di luar menu
        document.addEventListener('click', function(event) {
            if (!event.target.closest('#menu')) {
                document.querySelectorAll('.sub-menu').forEach(sub => {
                    sub.style.display = 'none'; // Sembunyikan semua submenu
                });
            }
        });
    </script>
